mapper.select:
select a.id, a.hazard_factors_name as hazardFactorsName, a.hazard_factors_code as hazardFactorsCode, a.duty_status as dutyStatus, d.dict_label as dutyStatusName, case when a.association_type = '1' then wm_concat(c.zyb_code || c.basic_project_name) when a.association_type in ('2', '3') then wm_concat(e.code || e.value) end as itemName, a.enable_status as enableStatus, a.update_time as updateTime, a.update_by as updateBy, a.association_type as associationType, a.symptom_focus as symptomFocus, a.physical_examination as physicalExamination, a.inspection_cycle as inspectionCycle, a.evaluation_criterion as evaluationCriterion, a.shine_source as shineSource, a.shine_type as shineType from tj_hazard_factors_require a left join tj_hazard_factors_item b on a.id = b.factors_id and b.del_flag = '0' left join tj_basic_project c on b.item_id = c.id left join sys_dict_data d on a.duty_status = d.dict_value and d.dict_type = 'bus_duty_status' left join tj_occupational_dict e on b.item_id = e.id where a.del_flag = '0'
and a.duty_status = #{bo.dutyStatus}
and a.hazard_factors_code = #{bo.hazardFactorsCode}
and a.association_type = #{bo.associationType}
and a.hazard_factors_name like '%' || #{bo.hazardFactorsName} || '%'
group by a.id, a.hazard_factors_name, a.hazard_factors_code, a.duty_status, d.label, a.enable_status, a.update_time, a.update_user, a.association_type, a.symptom_focus, a.physical_examination, a.inspection_cycle, a.evaluation_criterion, a.shine_source, a.shine_type order by a.update_time desc

select count(1) from tj_package a , tj_package_hazards b
where a.sfqy = '0'
  and a.del_flag = '0'
  and b.del_flag = '0'
  and b.hazard_factors_code = #{hazardFactorsCode}
  and a.id = b.package_id
  and a.duty_status = #{dutyStatus}

    and a.shine_source = #{shineSource}


    and a.shine_type = #{shineType}
