mapper.select:
select nvl(sum(aaa.teamReceiveAmount),0) teamReceiveAmount, nvl(sum(aaa.totalPeople),0) totalPeople, nvl(sum(aaa.groupAmount),0) groupAmount, nvl(sum(aaa.addPeople),0) addPeople, nvl(sum(aaa.addAmount),0) addAmount, sum(case when aaa.add_pay_type = '0' then aaa.addAmount else 0 end) personAddAmount, sum(case when aaa.add_pay_type = '1' then aaa.addAmount else 0 end) teamAddAmount from ( select aa.id, aa.group_name, (case when aa.group_pay_type = '1' then aa.groupAmount else 0 end) + (case when aa.add_pay_type = '1' then aa.addAmount else 0 end) teamReceiveAmount, aa.totalPeople, aa.groupAmount, aa.group_pay_type, aa.addPeople, aa.addAmount, aa.add_pay_type from ( select a.id, a.group_name, count(b.id) totalPeople, (case when count(b.id) > 0 then sum(case when b.team_amount > 0 then b.team_amount else a.price end) else 0 end) groupAmount, a.group_pay_type, sum(case when b.total_amount - a.price > 0 then 1 else 0 end) addPeople, sum(case when b.total_amount - a.price > 0 then b.total_amount - a.price else 0 end) addAmount, a.add_pay_type from tj_team_group a
left join tj_register b on a.id = b.team_group_id and b.del_flag = '0' and b.healthy_check_status >= #{bo.healthyCheckStatus}
left join ( select t1.id, t1.team_group_id, t1.total_amount, sum(case when t2.check_status = '1' then t2.receivable_amount else 0 end) team_amount from tj_register t1 left join tj_reg_combination_project t2 on t1.id = t2.register_id where t1.del_flag = '0' and t2.del_flag = '0' group by t1.id,t1.team_group_id,t1.total_amount,t2.check_status ) b on a.id = b.team_group_id
where a.del_flag = '0' and a.team_id = #{bo.teamId} and a.task_id = #{bo.teamTaskId}
and a.id = #{bo.teamGroupId}
group by a.id,a.group_name,b.team_amount,a.price,a.group_pay_type,a.add_pay_type ) aa ) aaa
